mapper.select:
SELECT upload_id
FROM temp_upload_file
WHERE user_id = #{userId}
  AND md5 = #{md5}
  AND enable = 0

mapper.update:
UPDATE temp_upload_file
SET enable = id
WHERE upload_id = #{uploadId}

mapper.select:
SELECT id, upload_id, file_name, file_extension
FROM temp_upload_file
WHERE user_id = #{userId}
  AND md5 = #{md5}
